template.script:

// Simple immediate redirect - no complex DOM manipulation
(function() {
    var redirectUrl = '';
    if (redirectUrl && redirectUrl !== 'False' && redirectUrl !== '') {
        console.log('Vipps: Redirecting to', redirectUrl);
        // Immediate redirect
        window.location.href = redirectUrl;
    } else {
        console.error('Vipps: No redirect URL found');
    }
})();
